import axios from 'axios';

// const apiUrl = "https://localhost:7255"
axios.defaults.baseURL = "https://localhost:7255";
export default {
  getTasks: async () => {
    const result = await axios.get(`/item`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    await axios.post(`${apiUrl}/item`,{
      "id": 0,
      "name":name,
      "isComplete": false
    }, {
      headers: {
        'Content-Type': 'application/json'
      }
    })  
    .then(response=>{
      console.log(response.data);
       return response.data;
   })
   .catch(err=>console.log(err));
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
   await axios.put(`${apiUrl}/item/${id}`,{
      "id": 0,
      "name": "string",
      "isComplete": isComplete
    }, {
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response=>{
      console.log(response.data);
      return response.data;
   })
   .catch(err=>console.log(err));
  },

  deleteTask:async(id)=>{
    await axios.delete(`${apiUrl}/item/${id}`)
    .then(response=>{
       console.log('deleteTask',response.data);
       return response.data;
    })
    .catch(err=>console.log(err));
  }
};
